import Vue from 'vue'
import Vuex from 'vuex'
import {getBuildingInfo} from '../api'
Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    buildingInfo
  },
  getters: {
  },
  mutations: {
  },
  actions: {
    async getInfo({ commit}){
      const resp = await getBuildingInfo()
      commit('buildingInfo', resp);

    }
  },
  modules: {
  }
})
