import Vue from 'vue'
import Vuex from 'vuex'
import { getBuildingInfosApi, getBuildingAreaApi, getFloorAreaApi, getFloorRoomListApi, getRoomAreaApi, getRoomTicketListApi, getControlEndPointListApi } from '../api'
Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    type: 'geographicContext',
    buildingInfos: {},
    totalArea: 0,
    floorArea: 0,
    floorList: [],
    roomList: [],
    roomInfo: [],
    roomAreas: [],
    ticketFloor: 0,
    controlEndPointList: []
  },
  getters: {
    type: (state) => state.type,
    buildingInfos: (state) => state.buildingInfos,
    floorList: (state) => state.floorList,
    totalArea: (state) => state.totalArea,
    floorArea: (state) => state.floorArea,
    roomList: (state) => state.roomList,
    roomAreas: (state) => state.roomAreas,
    roomInfo: (state) => state.roomInfo,
    controlEndPointList: (state) => state.controlEndPointList,
  },
  mutations: {
    setBuildingInfos: (state, buildingInfo) => (state.buildingInfos = buildingInfo),
    setBuildingArea: (state, area) => (state.totalArea = area),
    setFloorList: (state, floorList) => (state.floorList = floorList),
    setFloorArea: (state, area) => (state.totalArea = area),
    setFloorRoomList: (state, resp) => (state.roomList = resp),
    setRoomArea: (state, roomArray) => (state.roomAreas = roomArray),
    setRoomInfo: (state, info) => (state.roomInfo = info),
    setControlEndPointList: (state, resp) => (state.controlEndPointList = resp)
  },
  actions: {
    async getBuildingInfos({ commit }, node) {
      const resp = await getBuildingInfosApi();
      const info = () => {
        return { id: 0, name: '', area: 0, ticket: 0 }
      }
      const floorList = new Array(resp.children[0].children.length).fill(null).map(info);
      console.log('Building info', resp.type);

      if (node != undefined && node.type === 'geographicBuilding') {
        this.state.type = resp.type
      }
      resp.children.map(building => {
        building.children.map(async (floor, index) => {
          const floorInfo = await getFloorAreaApi(floor.dynamicId)
          console.log(floor.name);
          floorList[index].name = floor.name
          floorList[index].area = floorInfo.area
          floorList[index].id = floor.dynamicId
        })
        console.log(floorList);
      });
      commit('setBuildingInfos', resp);
      commit('setFloorList', floorList)
    },
    async getBuildingArea({ commit }, node) {
      console.log('node', node);
      if (node != undefined && node.type === 'geographicBuilding') {
        const resp = await getBuildingAreaApi()
        console.log('area', resp.area);
        commit('setBuildingArea', resp.area);
      } else if (node === undefined || node.type === 'geographicContext') {
        console.log("geographicContext", node);
        const resp = await getBuildingAreaApi()
        commit('setBuildingArea', resp.area);
      }

    },
    async getFloorArea({ commit }, id) {
      const resp = await getFloorAreaApi(id)
      console.log('area', resp.area);
      commit('setFloorArea', resp.area);
    },
    async getFloorRoomList({ commit }, id) {
      const resp = await getFloorRoomListApi(id)
      console.log('room list', resp);
      if (resp.length) {
        this.state.type = resp[0].type;
      }

      function getOccupation(controlEndpoints) {
        for (const controlEndPoint of controlEndPoints) {
          for (const endpoint of controlEndPoint.endpoints) {
            if (endpoint.name === "Occupation") {
              return endpoint.currentValue
            }
          }
        }
        return false
      }
      const p = resp.map(async (room) => {
        let areaInfo = await getRoomAreaApi(room.dynamicId);
        let roomTicket = await getRoomTicketListApi(room.dynamicId);
        let controlEndPoints = await getControlEndPointListApi(room.dynamicId);

        return {
          id: room.dynamicId,
          name: room.name,
          area: areaInfo.area,
          ticket: roomTicket.length,
          occupation: getOccupation(controlEndPoints)
        }
      });
      const roomInfo =  await Promise.all(p)



      // const info = () => {
      //   return {id: 0, name: '', area: 0, ticket: 0, occupation: false}
      // }
      // const roomInfo = new Array(resp.length).fill(null).map(info);
      // this.roomList = resp;
      // const p =resp.map(async (room, index) => {
      //   let areaInfo = await getRoomAreaApi(room.dynamicId); 
      //   let roomTicket = await getRoomTicketListApi(room.dynamicId); 
      //   let controlEndPoints = await getControlEndPointListApi(room.dynamicId);
      //   controlEndPoints.forEach((controlEndPoint) =>{
      //     const endpoints = controlEndPoint.endpoints
      //     const occupation =  endpoints.filter( (endpoint) => endpoint.name === "Occupation");
      //     console.log(index, 'occupation', room.name,occupation[0].currentValue);
      //     roomInfo[index].occupation = occupation[0].currentValue
      //     console.log(roomInfo[index].occupation);
      //   })

      //   roomInfo[index].id = room.dynamicId;
      //   roomInfo[index].name = room.name;
      //   roomInfo[index].area = areaInfo.area;
      //   roomInfo[index].ticket = roomTicket.length;

      //   // roomArray.push(areaInfo.area);
      // });
      // await Promise.all(p)
      console.log('room info', roomInfo);
      commit('setFloorRoomList', p);
      // commit('setRoomArea',roomArray)
      commit('setRoomInfo', roomInfo)
    },
    // async getControlEndPointList({commit}, id){
    //   console.log(id);
    //   this.state.roomList.map(async (room) =>{
    //     // const resps = await getControlEndPointListApi(room.dynamicId);
    //     // resps.map((resp) =>{
    //     // const endpoints = resp.endpoints
    //     // const occupation =  endpoints.filter( (endpoint) => endpoint.name === "Occupation");
    //     // console.log('occupation',occupation[0].currentValue);
    //     // this.state.roomInfo.occupation = occupation[0].currentValue
    //     // console.log(this.state.roomInfo.occupation);
    //     // })
    //   })
    //   const resp = 1;
    //   commit('setControlEndPointList', resp)
    // }
    // async getRoomArea({commit}){
    //   this.roomList.forEach(room => {
    //     const resp =  getRoomAreaApi(room.dynamicId)
    //     console.log(resp);
    //     commit('setRoomArea', resp);
    //   });

    // }
  },


})
