import Vue from 'vue'
import Vuex from 'vuex'
import {getBuildingInfosApi, getBuildingAreaApi, getFloorAreaApi, getFloorRoomListApi, getRoomAreaApi, getRoomTicketListApi} from '../api'
Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    buildingInfos:{},
    buildingArea:0,
    floorArea:0,
    floorList:[{}],
    roomList:[],
    roomInfo:[{}],
    roomAreas:[]
  },
  getters: {
    buildingInfos: (state) => state.buildingInfos,
    floorList: (state) => state.floorList,
    buildingArea: (state) => state.buildingArea,
    floorArea: (state) => state.floorArea,
    roomList: (state) => state.roomList,
    roomAreas: (state) => state.roomAreas,
    roomInfo: (state) => state.roomInfo
  },
  mutations: {
    setBuildingInfos:(state, buildingInfo, floorList) => (
      state.buildingInfos = buildingInfo,
      state.floorList = floorList ),
    setBuildingArea:(state, area)=> (state.buildingArea = area),
    setFloorArea: (state, area) => (state.floorArea = area),
    setFloorRoomList: (state, resp) => (state.roomList = resp),
    setRoomArea: (state, roomArray)=> (state.roomAreas = roomArray),
    setRoomInfo: (state, info) => (state.roomInfo = info)
  },
  actions: {
    async getBuildingInfos({commit}){
      const resp = await getBuildingInfosApi()
      const info = () => {
        return {id: 0, name: '', area: 0, ticket: 0}
      }
      const floorList = new Array(resp.children[0].children.length).fill(null).map(info);
      console.log('Building info',resp);
      resp.children.map( building => {
        building.children.map( (floor, index) =>{
          console.log(floor.name);
          floorList[index].name = floor.name
          floorList[index].id = floor.dynamicId
        })
        console.log(floorList);
      });
      commit('setBuildingInfos', resp, floorList);

    },
    async getBuildingArea({commit}){
      const resp = await getBuildingAreaApi()
      // console.log('area',resp.area);
      commit('setBuildingArea', resp.area);

    },
    async getFloorArea({commit}, id){
      const resp = await getFloorAreaApi(id)
      console.log('area',resp.area);
      commit('setFloorArea', resp.area);

    },
    async getFloorRoomList({commit}, id){
      const resp = await getFloorRoomListApi(id)
      const info = () => {
        return {id: 0, name: '', area: 0, ticket: 0}
      }
      const roomInfo = new Array(resp.length).fill(null).map(info);
      this.roomList = resp;
      resp.map(async (room, index) => {
        let areaInfo = await getRoomAreaApi(room.dynamicId); 
        let roomTicket = await getRoomTicketListApi(room.dynamicId); 
        console.log(roomTicket);
        // roomInfo[index].name = room.name;
        // roomInfo[index].area = areaInfo.area      
        // roomInfo.fill({name: room.name, area: areaInfo.area})
        roomInfo[index].id = room.dynamicId;
        roomInfo[index].name = room.name;
        roomInfo[index].area = areaInfo.area;
        roomInfo[index].ticket = roomTicket.length;

        // roomArray.push(areaInfo.area);
      });
      console.log(roomInfo);
      // commit('setFloorRoomList', resp);
      // commit('setRoomArea',roomArray)
      commit('setRoomInfo',roomInfo )
    },
    // async getRoomArea({commit}){
    //   this.roomList.forEach(room => {
    //     const resp =  getRoomAreaApi(room.dynamicId)
    //     console.log(resp);
    //     commit('setRoomArea', resp);
    //   });

    // }
  },

})
