import Vue from 'vue'
import Vuex from 'vuex'
import {getBuildingInfosApi} from '../api'
Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    buildingInfos:[]
  },
  getters: {
    buildingInfos: (state) => state.buildingInfos
  },
  mutations: {
    buildingInfos:(state, buildingInfo) => (state.buildingInfos = buildingInfo )
  },
  actions: {
    async getBuildingInfos({commit}){
      const resp = await getBuildingInfosApi()
      console.log(resp[0]);
      commit('buildingInfos', resp);

    }
  },
  modules: {
  }
})
